import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import ProgressBar from './progressBar';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      act: 0,
      index: '',
      datas: [],
      percentage: 1,
      progress: '',
      searchString: ''
    }

  }

  componentDidMount() {
    this.refs.search.focus();
    this.refs.name.focus();
    this.refs.startDate.focus();
    this.refs.releaseDate.focus();
    this.refs.description.focus();
  }

  handleChange() {
    this.setState({
      searchString: this.refs.search.value
    });
  }
  fSubmit = (e) => {


    e.preventDefault();

    let datas = this.state.datas;
    let name = this.refs.name.value;
    let startDate = this.refs.startDate.value;
    let releaseDate = this.refs.releaseDate.value;
    let description = this.refs.description.value;

    console.log(datas);

    if (this.state.act === 0) { //new
      let data = {
        name, startDate, releaseDate, description
      }

      datas.push(data);
    } else {                    //update
      let index = this.state.index;
      datas[index].name = name;
      datas[index].startDate = startDate;
      datas[index].releaseDate = releaseDate;
      datas[index].description = description;
    }

    this.setState({
      datas: datas
    });
    this.setState({
      progress: ((this.state.percentage === 0 ? 'UnRelease' : (this.state.percentage === 100 ? 'Released' : 'InProgress')))
    })
    this.refs.myForm.reset();
    this.refs.name.focus();
    this.refs.startDate.focus();
    this.refs.releaseDate.focus();
    this.refs.description.focus();
  }

  fRemove = (i) => {
    let datas = this.state.datas;
    datas.splice(i, 1);
    this.setState({
      datas: datas
    });

    this.refs.myForm.reset();
    this.refs.name.focus();
    this.refs.startDate.focus();
    this.refs.releaseDate.focus();
    this.refs.description.focus();
  }

  fEdit = (i) => {
    let data = this.state.datas[i];
    this.refs.name.value = data.name;
    this.refs.startDate.value = data.startDate;
    this.refs.releaseDate.value = data.releaseDate;
    this.refs.description.value = data.description;

    this.setState({
      act: 1,
      index: i
    })

    this.refs.name.focus();
    this.refs.startDate.focus();
    this.refs.releaseDate.focus();
    this.refs.description.focus();
  }

render(){
    return (
      <div className="App">

  <div class="row">
            <div className="col-lg-12 grid-margin">
              <div className="card">
                <div className="card-body">
                  <h4 className="card-title">Orders</h4>
                  <div className="table-responsive">
                    <table className="table table-bordered">
                      <thead>
                        <tr>
                          <th>
                            #
                          </th>
                          <th>
                            First name
                          </th>
                          <th>
                            Progress
                          </th>
                          <th>
                            Amount
                          </th>
                          <th>
                            Sales
                          </th>
                          <th>
                            Deadline
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        <tr>
                          <td class="font-weight-medium">
                            1
                          </td>
                          <td>
                            Herman Beck
                          </td>
                          <td>
                            <div className="progress">
                            <div class="progress-bar bg-danger progress-bar-striped" role="progressbar" aria-valuenow="35" aria-valuemin="0"
                                aria-valuemax="100"></div>
                            </div>
                          </td>
                          <td>
                            $ 77.99
                          </td>
                          <td className="text-danger"> 53.64%
                            <i className="mdi mdi-arrow-down"></i>
                          </td>
                          <td>
                            May 15, 2015
                          </td>
                        </tr>

                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>



      </div>
    );
  }
}

export default App;
